sys_variable_value.value:
(function execute(inputs, outputs) {
  var result;
  var url = inputs.conn_url;
     var baseUrlPattern = /^https?:\/\/[a-z\:0-9-.]+/;
    var match = baseUrlPattern.exec(url);
    if (match != null) {
        result = match[0];
    }
    if (result.length > 0) {
        url = url.replace(result, "");
    }
  inputs.file_name = inputs.file_name.replace(/ /g,"+");

  if(inputs.file_name == "" || inputs.file_name == null)
    outputs.name = "MyFile";
  else 
    outputs.name = inputs.file_name;

  outputs.resource_path = url;
})(inputs, outputs);

