sys_variable_value.value:
(function execute(inputs, outputs) {

  if(inputs.action != "Priority change")
     return
/*
  coresponding pairs SN - JIRA,
  Critical(1) 	- Critical, 
  High 			- High, 
  Moderate 		- Medium, 
  Low(4) 		- Low


*/ 
  var jiraPriority = inputs.priority==1?"Critical":inputs.priority==2?"High":inputs.priority==3?"Medium":"Low"

  var payloadAsObject = {"update":{"priority":[{"set":{"name" : jiraPriority}}]}};
    outputs.url_suffix = '';
    outputs.url_action = 'issue';



  outputs.payload = JSON.stringify(payloadAsObject);

})(inputs, outputs);

