sys_variable_value.value:
(function execute(inputs, outputs) {

  if(inputs.action == '')
    return

  var payloadAsObject = {};

  if(inputs.action == 'Transition+comment'){
    payloadAsObject.update = {};
    payloadAsObject.update.comment = [{"add":{"body": inputs.comment}}]
    payloadAsObject.transition = {"id":inputs.transition};
    outputs.url_suffix = '/transitions';
  }
  if(inputs.action == 'Transition+comment+cField'){
    payloadAsObject.update = {};
    payloadAsObject.fields = {};
    payloadAsObject.update.comment = [{"add":{"body": inputs.comment}}]
    payloadAsObject.transition = {"id":inputs.transition};
    payloadAsObject.fields[inputs.customFieldID] = inputs.customFieldValue;
    outputs.url_suffix = '/transitions';
  }


  if(inputs.action == 'Transition'){
    payloadAsObject = {"transition":{"id":inputs.transition}}
    outputs.url_suffix = '/transitions';
  }

  if(inputs.action == 'Comment'){
    payloadAsObject = {"body": inputs.comment}
    outputs.url_suffix = '/comment';    outputs.url_action = 'issue';
  }

    outputs.url_action = 'issue';
  outputs.payload = JSON.stringify(payloadAsObject);

})(inputs, outputs);

