sys_variable_value.value:
(function execute(inputs, outputs) {

  var payloadAsObject = {};
  payloadAsObject.fields = {};

  payloadAsObject.fields.project = {"key":inputs.project};
  payloadAsObject.fields.summary = inputs.summary;
  payloadAsObject.fields.description = inputs.description;
  payloadAsObject.fields.issuetype = {"name":inputs.issuetype};

  if(inputs.externalID)
    payloadAsObject.fields.customfield_11012 = inputs.externalID;

  if(inputs.vendor)
    payloadAsObject.fields.customfield_11705 = inputs.vendor;

  if(inputs.service && gs.getProperty('x_kopas_ais_jira_s.ais_jira_send_service')=='true')
    payloadAsObject.fields.customfield_11807 = inputs.service;

  var newArray = [];
  var incidentGR = new GlideRecord('incident');
  if(incidentGR.get('number', inputs.externalID)){
    if(incidentGR.watch_list){
      var AJUtils = new x_kopas_ais_jira_s.AIS_JiraIntegration_Utils();
      var jiraProdUrl = gs.getProperty('x_kopas_ais_jira_s.AIS_JIRA_production_url');
      var jiraIssueUrl = jiraProdUrl+'/rest/api/2/user/search?username=';
      var watchListArray = incidentGR.watch_list.split(',');;
      watchListArray.forEach(function(item){
        userGR = new GlideRecord('sys_user');
        if(userGR.get(item)){
          if(AJUtils.userExistsInJira(jiraIssueUrl+userGR.user_name.getDisplayValue())){
            var watchListObject = {"name":userGR.user_name.getDisplayValue()};
            newArray.push(watchListObject)
          }
        }
      })
    }
    if(newArray.length > 0)
      payloadAsObject.fields.customfield_10405 = newArray;
  }

  if(inputs.incidentURL)
    payloadAsObject.fields.customfield_11706 = inputs.incidentURL;

  if(inputs.reporter)
    payloadAsObject.fields.reporter = {"name": inputs.reporter};

  if(inputs.priority){
    /*
  coresponding pairs SN - JIRA,
  Critical(1) 	- Critical, 
  High 			- High, 
  Moderate 		- Medium, 
  Low(4) 		- Low
*/ 
    var jiraPriority = inputs.priority==1?"Critical":inputs.priority==2?"High":inputs.priority==3?"Medium":"Low"
    payloadAsObject.fields.priority = {"name": jiraPriority};
  }
  outputs.payload_body = JSON.stringify(payloadAsObject);

})(inputs, outputs);

